mapper.insert:
INSERT INTO projet.type_de_machine (model_type_m, manufacturier_type_m)
VALUES (#{machine_template.model_type_m}, #{machine_template.manufacturier_type_m});

INSERT INTO projet.machine (image_machine, id_type_m)
VALUES (#{machine_template.image_machine}, (SELECT MAX(id_type_m) FROM projet.type_de_machine));

INSERT INTO projet.usager_x_machines (id_usager, id_machine)
VALUES (#{machine_template.id_usager}, (SELECT MAX(id_machine) FROM projet.machine));

mapper.delete:
DELETE
FROM projet.usager_x_machines
WHERE id_machine = #{id_machine};

DELETE
FROM projet.machine
WHERE id_machine = #{id_machine};
